mapper.select:
SELECT * FROM POST_TB AS a JOIN USER_TB AS b ON a.WRT_NO = b.USER_NO_PK
ORDER BY POST_NO_PK DESC
LIMIT #{start} , #{pageSize}

mapper.update:


UPDATE
	POST_TB
SET
	TITLE		=	#{title}
,	CONTENT		=	#{content}
,	WRT_RE_DATE	=	NOW()
WHERE
	POST_NO_PK 	= 	#{postNum}

mapper.insert:


INSERT INTO POST_TB
(
		TITLE
	,	CONTENT
	,	WRT_NO
	,	WRT_DATE
	,	WRT_RE_DATE
)

VALUES

(
		#{title}
	,	#{content}
	,	#{writer}
	,	NOW()
	,	NOW()
)



INSERT INTO FILE_TB
(
		POST_NO
	,	ORIGINAL_NAME
	,	STORED_NAME
	,	FILE_SIZE

)
VALUES
(
		#{postNum}
	,	#{originalFileName}
	,	#{storedFileName}
	,	#{fileSize}


)

mapper.update:
UPDATE FILE_TB
SET DEL_FL = "Y"
WHERE POST_NO = #{postNo}

UPDATE FILE_TB
SET DEL_FL = "Y"
WHERE FILE_NO_PK = #{fileNo}
